SELECT <FIELDS/> 
FROM <TABLE/> 
WHERE <FIELD name='period'/> == ${period}
AND <FIELD name='domain'/> = ${domain}
AND <FIELD name='name'/> = ${name}

SELECT <FIELDS/> 
FROM <TABLE/> 
WHERE <FIELD name='period'/> >= ${start-date}
AND <FIELD name='period'/> < ${end-date}
AND <FIELD name='name'/> = ${name} 
AND type = 1

