SELECT <FIELDS/> 
FROM <TABLE/> 
WHERE <FIELD name='period'/> = ${period}
<IF type='NOT_NULL' field='domain'>
	AND <FIELD name='domain'/> = ${domain}
</IF>
<IF type='NOT_NULL' field='name'>
	AND <FIELD name='name'/> = ${name}
</IF>	
AND type = 1

